pragma solidity 0.4.18;

import './Profilable.sol';

library Roles {
    struct Role {
        mapping (address => bool) bearer;
    }

    function add(Role storage role, address addr) internal {
        role.bearer[addr] = true;
    }

    function remove(Role storage role, address addr) internal {
        role.bearer[addr] = false;
    }

    function check(Role storage role, address addr) view internal {
        require(role.bearer[addr]);
    }

    function softCheck(Role storage role, address addr) view internal returns (bool) {
        return role.bearer[addr];
    }
}

contract B is Profilable {
    using Roles for Roles.Role;
    // role name to Role registry
    mapping (string => Roles.Role) roles;

    function profile()
        public
    {

        // adding and removal consumes memory so measure how much
        Roles.Role storage role = roles['owner'];
        role.add(address(1));
        role.add(address(2));
        role.add(address(3));
        role.add(address(4));
        role.add(address(5));
        role.add(address(6));

        // checking should be O(1)
        require(role.softCheck(address(1)));
    }
}
